import React, { useEffect, useRef } from "react";
import Chart from "chart.js/auto";

function Reports() {
  const pieChartRef = useRef(null);
  const barChartRef = useRef(null);

  useEffect(() => {
    // Data for the charts
    const data = {
      labels: ["Facebook", "Instagram", "Twitter", "Telegram"],
      datasets: [
        {
          data: [2000000000, 1500000000, 1000000000, 500000000],
          backgroundColor: ["#8884d8", "#82ca9d", "#ffc658", "#ff7300"],
        },
      ],
    };

    // Create the Pie Chart
    const pieChart = new Chart(pieChartRef.current, {
      type: "pie",
      data: data,
    });

    // Create the Bar Chart
    const barChart = new Chart(barChartRef.current, {
      type: "bar",
      data: {
        labels: data.labels,
        datasets: [
          {
            label: "Users",
            data: data.datasets[0].data,
            backgroundColor: data.datasets[0].backgroundColor,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    });

    // Clean up the charts when the component unmounts
    return () => {
      pieChart.destroy();
      barChart.destroy();
    };
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Social Media Users</h1>
      <div className="App">
        <canvas ref={pieChartRef} style={{ width: "100px", height: "400px" }} />
        <canvas ref={barChartRef} style={{ width: "100px", height: "300px" }} />
      </div>
    </div>
  );
}

export default Reports;
