                ))}
              </select>

              <div className="signselect-arrow"></div>
            </div>
          </div>

          <input type="submit" value="Update" className="signsubmit-btn" />
          <Link to={`/admin?email=${email}`} className="signforgot-pw">
            Go Back
          </Link>
        </form>
      </div>
    </div>
  );
}

export default EnrollCourses;
