import React, { useState } from "react";
import axios from "axios";

function EditCourse({ course, onSave, onCancel }) {
  const [editedCourse, setEditedCourse] = useState({ ...course });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedCourse({ ...editedCourse, [name]: value });
  };

  const handleSave = () => {
    axios
      .post("https://sxt9335.uta.cloud/editCourse.php", editedCourse)
      .then((response) => {
        onSave(response.data); // Assuming the server returns the updated course
      })
      .catch((error) => {
        console.error("Error updating course:", error);
      });
  };

  return (
    <div className="edit-course-form">
      <input
        type="text"
        name="course_name"
        value={editedCourse.course_name}
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="instructor_name"
        value={editedCourse.instructor_name}
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="course_period"
        value={editedCourse.course_period}
        onChange={handleInputChange}
      />
      <button onClick={handleSave}>Save</button>
      <button onClick={onCancel}>Cancel</button>
    </div>
  );
}

export default EditCourse;
