import React, { useEffect, useRef } from "react";
import "../css/style.css"; // Import your CSS file;
import Chart from "chart.js/auto";

function Reports() {
  const pieChartRef = useRef(null);
  const barChartRef = useRef(null);

  // Declare variables to hold references to the created charts
  let pieChartInstance;
  let barChartInstance;

  useEffect(() => {
    // Data for the charts
    const data = {
      labels: ["Facebook", "Instagram", "Twitter", "Telegram"],
      datasets: [
        {
          data: [2000000000, 1500000000, 1000000000, 500000000],
          backgroundColor: ["#8884d8", "#82ca9d", "#ffc658", "#ff7300"],
        },
      ],
    };

    // Destroy existing charts if they exist
    if (pieChartInstance) {
      pieChartInstance.destroy();
    }

    if (barChartInstance) {
      barChartInstance.destroy();
    }

    // Create the Pie Chart
    pieChartInstance = new Chart(pieChartRef.current, {
      type: "pie",
      data: data,
      options: {
        maintainAspectRatio: false,
        responsive: true,
        aspectRatio: 1,
      },
    });

    // Create the Bar Chart
    barChartInstance = new Chart(barChartRef.current, {
      type: "bar",
      data: {
        labels: data.labels,
        datasets: [
          {
            label: "Users",
            data: data.datasets[0].data,
            backgroundColor: data.datasets[0].backgroundColor,
          },
        ],
      },
      options: {
        maintainAspectRatio: false,
        responsive: true,
      },
    });
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Social Media Users</h1>
      <div className="App">
        <canvas ref={pieChartRef} />
        <canvas ref={barChartRef} />
      </div>
    </div>
  );
}

export default Reports;
