import { useState, useEffect, React } from "react";
import "../css/student.css"; // Import your CSS file here
import avatar1 from "../assets/avatar4.jpeg";

import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";
import backgroundImage from "../assets/cards.jpg";
import axios from "axios";
import DropDownProfile from "./dropdown";
import Swal from "sweetalert2";
function Student() {
  const [openProfile, setOpenProfile] = useState(false);
  const { search } = useLocation();
  const searchParams = new URLSearchParams(search);
  const email = searchParams.get("email");
  const [classes, setClasses] = useState([]);
  const [program, setProgram] = useState([]);
  const [name, setName] = useState("");

  const handleCloseModal = () => {
    setOpenProfile(false);
  };
  const loadPrograms2 = async () => {
    const programResults = await axios.get(
      "https://sxt9335.uta.cloud/viewPrograms2.php"
    );
    setProgram(programResults.data.AllPrograms);
    console.log("inside proframs view");
    console.log(programResults.data.AllPrograms);
  };

  useEffect(() => {
    loadPrograms2();
  }, []);
  const loadPrograms = async () => {
    fetch("https://sxt9335.uta.cloud/getName.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `email=${email}`,
    })
      .then((response) => response.text())
      .then((data) => {
        setName(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  useEffect(() => {
    loadPrograms();
  }, []);
  const loadClasses = () => {
    fetch("https://sxt9335.uta.cloud/viewClassesEnrolled.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `email=${email}`,
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setClasses(data.classes);
        } else {
          console.error("Server response indicated an error.");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  useEffect(() => {
    loadClasses();
  }, []);
  const loadExams = () => {
    fetch("https://sxt9335.uta.cloud/viewClassesEnrolled.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `email=${email}`,
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setClasses(data.classes);
        } else {
          console.error("Server response indicated an error.");
