import React, { useEffect, useRef } from "react";
import "../css/style.css"; // Import your CSS file;
import Chart from "chart.js/auto";

function Reports() {
  const pieChartRef = useRef(null);
  const barChartRef = useRef(null);

  useEffect(() => {
    // Data for the charts
    const data = {
      labels: ["Facebook", "Instagram", "Twitter", "Telegram"],
      datasets: [
        {
          data: [2000000000, 1500000000, 1000000000, 500000000],
          backgroundColor: ["#8884d8", "#82ca9d", "#ffc658", "#ff7300"],
        },
      ],
    };

    // Create the Pie Chart
    new Chart(pieChartRef.current, {
      type: "pie",
      data: data,
      options: {
        maintainAspectRatio: false, // Allow chart to resize
        responsive: true, // Enable responsiveness
        aspectRatio: 1, // Set the aspect ratio (1:1 for a square chart)
      },
    });

    // Create the Bar Chart
    new Chart(barChartRef.current, {
      type: "bar",
      data: {
        labels: data.labels,
        datasets: [
          {
            label: "Users",
            data: data.datasets[0].data,
            backgroundColor: data.datasets[0].backgroundColor,
          },
        ],
      },
      options: {
        maintainAspectRatio: false, // Allow chart to resize
        responsive: true, // Enable responsiveness
      },
    });
  }, []);

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Social Media Users</h1>
      <div className="App">
        <canvas ref={pieChartRef} width={100} height={100} />
        <canvas ref={barChartRef} />
      </div>
    </div>
  );
}

export default Reports;
