import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import Swal from "sweetalert2";
import axios from "axios"; // Import Axios for making API requests

function EditCourse() {
  const history = useHistory(); // Use history for navigation

  const { search } = useLocation();
  const searchParams = new URLSearchParams(search);
  const email = searchParams.get("email");
  const initialCourse = {
    course_name: searchParams.get("course_name"),
    course_id: searchParams.get("course_id"),
    instructor_name: searchParams.get("instructor_name"),
    course_period: searchParams.get("course_period"),
  };

  const [course, setCourse] = useState(initialCourse);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Send an API request to update the course data
    axios
      .post("YOUR_UPDATE_API_ENDPOINT", course) // Replace with your actual API endpoint
      .then((response) => {
        // Handle the success response
        Swal.fire("Success", "Course updated successfully", "success").then(
          (result) => {
            if (result.isConfirmed) {
              // Navigate back to the admin page
              history.push(`/admin?email=${email}`);
            }
          }
        );
      })
      .catch((error) => {
        // Handle errors here, e.g., show an error message
        Swal.fire("Error", "Failed to update course", "error");
        console.error("Error updating course:", error);
      });
  };

  const handleInputChange = (e) => {
    // Update the course state when input fields change
    const { name, value } = e.target;
    setCourse({
      ...course,
      [name]: value,
    });
  };

  return (
    <div>
      <html lang="en">
        <head></head>
        <body className="signbody">
          <header>
            <Link to="/home" data-after="About">
              <img className="signuplogo" src={avatar} alt="skillbridge" />
            </Link>
          </header>
          <div className="signlogin-wrapper">
            <form
              method="POST"
              className="signform"
              onSubmit={handleFormSubmit}
            >
              <h3>Edit Courses</h3>
              <div className="signinput-group">
                <input
                  type="text"
                  name="course_name"
                  id="loginUser"
                  value={course.course_name}
                  onChange={handleInputChange}
                  required
                />
                <label for="loginUser">Course Name</label>
              </div>
              <div className="signinput-group">
                <input
                  type="text"
                  name="course_id"
                  id="loginUser"
                  value={course.course_id}
                  onChange={handleInputChange}
                  required
                />
                <label for="loginUser">Course ID</label>
              </div>
              <div className="signinput-group">
                <input
                  type="text"
                  name="instructor_name"
                  id="loginUser"
                  value={course.instructor_name}
                  onChange={handleInputChange}
                  required
                />
                <label for="loginUser">Instructor Name</label>
              </div>
              <div className="signinput-group">
                <input
                  type="text"
                  name="course_period"
                  id="loginUser"
                  value={course.course_period}
                  onChange={handleInputChange}
                  required
                />
                <label for="loginUser">Course Period</label>
              </div>

              <input
                type="submit"
                value="Continue"
                className="signsubmit-btn"
              />
              <Link to={`/admin?email=${email}`} className="signforgot-pw">
                Go Back
              </Link>
            </form>
          </div>
        </body>
      </html>
    </div>
  );
}

export default EditCourse;
