import React, { useState } from "react";
import "../css/login.css";
import "../App.css";
import { Link } from "react-router-dom";
import avatar from "../assets/logo.png";
import avatar1 from "../assets/avatar.png";
function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (email != "" && password != "") {
      fetch("https://sxt9335.uta.cloud/signup.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",
        },
        body: `email=${email}&password=${password}`,
      })
        .then((response) => response.text())
        .then((data) => {
          if (data === "Login successful") {
            window.location.href = "/admin";
          } else if (data === "Login failed") {
            alert("Entered credentials are not correct. Please check");
            window.location.href = "/login";
          }
        })
        .catch((error) => {
          console.error("Error:", error);
          alert("An error occurred while registering the user.");
        });
    } else {
      alert("Error", "Please enter a email and password to continue.");
    }
  };
  return (
    <div className="loginclass">
      <header>
        {/* <a href="index.html">
          <img className="loginlogo" src="images/logo.png" alt="" />
        </a> */}
        <Link to="/home" data-after="About">
          <img className="loginlogo" src={avatar} alt="skillbridge" />
        </Link>
      </header>
      <div className="login-wrapper">
        <form onSubmit={handleFormSubmit} className="loginform">
          <img src={avatar1} alt="skillbridge" />
          <h2>Login</h2>
          <div className="logininput-group">
            <input
              type="text"
              name="email"
              onChange={(e) => setEmail(e.target.value)}
              id="loginUser"
              required
            />
            <label htmlFor="loginUser">Email</label>
          </div>
          <div className="logininput-group">
            <input
              type="password"
              name="password"
              onChange={(e) => setPassword(e.target.value)}
              id="loginPassword"
              required
            />
            <label htmlFor="loginPassword">Password</label>
          </div>
          <input type="submit" value="Login" className="loginsubmit-btn" />
          <a href="#forgot-pw" class="forgot-pw">
            Need help?
          </a>
          <p className="login-para" style={{ fontSize: "1.5rem" }}>
            New to SkillBridge?<span> </span>
            <Link to="/signup" className="login-link">
              Sign up now
            </Link>
          </p>
        </form>
        <div id="forgot-pw">
          <form action="" className="loginform">
            <a href="#" className="loginclose">
              &times;
            </a>
            <h2>Reset Password</h2>
            <div className="logininput-group">
              <input type="email" name="email" id="email" required />
              <label htmlFor="email">Email</label>
            </div>
            <input type="submit" value="Submit" className="loginsubmit-btn" />
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
