import React, { useEffect, useState } from "react";
import ScrollToBottom from "react-scroll-to-bottom";
import { useLocation } from "react-router-dom";
import { Link } from "react-router-dom";

function Chat({ socket, username, room }) {
  const [currentMessage, setCurrentMessage] = useState("");
  const [messageList, setMessageList] = useState([]);

  const sendMessage = async () => {
    if (currentMessage !== "") {
      const messageData = {
        room: room,
        author: username,
        message: currentMessage,
        time:
          new Date(Date.now()).getHours() +
          ":" +
          new Date(Date.now()).getMinutes(),
      };

      await socket.emit("send_message", messageData);
      setMessageList((list) => [...list, messageData]);
      setCurrentMessage("");
    }
  };

  useEffect(() => {
    const handleReceivedMessage = (data) => {
      setMessageList((list) => [...list, data]);
      console.log(data);
    };

    socket.on("recieve_message", handleReceivedMessage);

    return () => {
      // Clean up the event listener when the component unmounts
      socket.off("recieve_message", handleReceivedMessage);
    };
  }, [socket]);

  return (
    <div className="stylecontainer">
      <div className="stylesidebar">
        <link
          rel="stylesheet"
          href="https://use.fontawesome.com/releases/v5.15.4/css/all.css"
          integrity="sha384-DyZ88mC6Up2uqS4h/KRgHuoeGwBcD4Ng9SiP4dIRy0EXTlnuz47vAwmeGwVChigm"
          crossOrigin="anonymous"
        />
        <ul>
          <li>
            <Link to="/home">
              <i className="fas fa-book"></i>
              <div className="styletitle">SkillBridge</div>
            </Link>
          </li>
          <li>
            <a href="#">
              <i className="fas fa-th-large"></i>
              <div className="styletitle">Admin Dashboard</div>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fas fa-tag"></i>
              <div className="styletitle">Manage Profiles</div>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fas fa-tag"></i>
              <div className="styletitle">Trobleshoot Issues</div>
            </a>
          </li>
          <li>
            <Link to="">
              <i className="fas fa-tag"></i>
              <div className="styletitle">Grant Permissions</div>
            </Link>
          </li>
          <li>
            <Link to={`/edit?email=${email}`}>
              <i className="fas fa-user"></i>
              <div className="styletitle">Edit Profile</div>
            </Link>
          </li>
          <li>
            <Link to={`/messagechat?email=${email}`}>
              <i className="fas fa-user"></i>
              <div className="styletitle">WebChat</div>
            </Link>
          </li>
          <li>
            <Link to="/login">
              <i className="fas fa-user"></i>
              <div className="styletitle">Logout</div>
            </Link>
          </li>
        </ul>
      </div>
      <div className="chat-window">
        <div className="chat-header">
          <p>Live Chat</p>
        </div>
        <div className="chat-body">
          <ScrollToBottom className="message-container">
            {messageList.map((messageContent) => {
              return (
                <div
                  className="message"
                  id={username === messageContent.author ? "you" : "other"}
                >
                  <div>
                    <div className="message-content">
                      <p>{messageContent.message}</p>
                    </div>
                    <div className="message-meta">
                      <p id="time">{messageContent.time}</p>
                      <p id="author">{messageContent.author}</p>
                    </div>
                  </div>
                </div>
              );
            })}
          </ScrollToBottom>
        </div>
        <div className="chat-footer">
          <input
            type="text"
            value={currentMessage}
            placeholder="Type a message...."
            onChange={(e) => {
              setCurrentMessage(e.target.value);
            }}
            onKeyPress={(e) => {
              e.key === "Enter" && sendMessage();
            }}
          />
          <button onClick={sendMessage}>&#9658;</button>
        </div>
      </div>
    </div>
  );
}

export default Chat;
